      if (this.chatId != msg.chat.id) return;

      const arg = match[1]; // the captured "whatever"
      const resp = arg ? `Sup, ${arg}!` : "";
      const reply = `${resp}${this.randomEmoji()}${this.randomEmoji()}${this.randomEmoji()}${this.randomEmoji()}${this.randomEmoji()}`;
